// eslint.config.js
import path from 'node:path';

// Plugins
import tseslint from '@typescript-eslint/eslint-plugin';
import tsparser from '@typescript-eslint/parser';
import stylistic from '@stylistic/eslint-plugin';
import importPlugin from 'eslint-plugin-import';
import promise from 'eslint-plugin-promise';
import security from 'eslint-plugin-security';
import sonarjs from 'eslint-plugin-sonarjs';
import unicorn from 'eslint-plugin-unicorn';
import vitest from 'eslint-plugin-vitest';
import prettier from 'eslint-plugin-prettier';

// Configs
import airbnbBase from 'eslint-config-airbnb-base';
import airbnbTsBase from 'eslint-config-airbnb-typescript/base';
import prettierConfig from 'eslint-config-prettier';

// Utility to merge configs
const merge = (...configs) => configs.flat();

export default [
  {
    ignores: [
      'dist',
      'build',
      'node_modules',
      'coverage',
      '*.d.ts',
      '*.min.js',
      '**/*.gen.ts',
      '**/*.generated.ts',
      '**/__generated__/**',
      '.turbo',
      '.next',
      '.vite',
      '.cache',
    ],
  },
  {
    files: ['**/*.{ts,tsx,js,jsx}'],
    languageOptions: {
      parser: tsparser,
      parserOptions: {
        ecmaVersion: 2022,
        sourceType: 'module',
        project: ['./tsconfig.json', './packages/*/tsconfig.json'],
        tsconfigRootDir: path.resolve(),
        projectService: true,
      },
      globals: {
        NodeJS: true,
      },
    },
    plugins: {
      '@typescript-eslint': tseslint,
      '@stylistic': stylistic,
      import: importPlugin,
      promise,
      security,
      sonarjs,
      unicorn,
      vitest,
      prettier,
    },
    rules: {
      // Prettier
      'prettier/prettier': 'error',

      // TS
      '@typescript-eslint/explicit-function-return-type': ['error', {
        allowExpressions: true,
        allowTypedFunctionExpressions: true,
        allowHigherOrderFunctions: true,
      }],
      '@typescript-eslint/explicit-module-boundary-types': 'warn',
      '@typescript-eslint/no-explicit-any': 'error',
      '@typescript-eslint/only-throw-error': 'error',
      '@typescript-eslint/no-unused-vars': ['error', {
        argsIgnorePattern: '^_',
        varsIgnorePattern: '^_',
        caughtErrorsIgnorePattern: '^_',
      }],
      '@typescript-eslint/consistent-type-imports': ['error', {
        prefer: 'type-imports',
        fixStyle: 'separate-type-imports',
      }],
      '@typescript-eslint/consistent-type-exports': ['error', {
        fixMixedExportsWithInlineTypeSpecifier: true,
      }],
      '@typescript-eslint/no-floating-promises': ['error', { ignoreVoid: true, ignoreIIFE: true }],
      '@typescript-eslint/no-misused-promises': ['error', { checksVoidReturn: { attributes: false } }],
      '@typescript-eslint/await-thenable': 'error',
      '@typescript-eslint/promise-function-async': 'error',

      // Style (brace style left to Prettier)
      'brace-style': 'off',
      'lines-between-class-members': 'off',
      '@typescript-eslint/lines-between-class-members': ['error', 'always', {
        exceptAfterSingleLine: true,
      }],

      // Naming
      '@typescript-eslint/naming-convention': ['error',
        { selector: 'default', format: ['camelCase'], leadingUnderscore: 'allow', trailingUnderscore: 'forbid' },
        { selector: 'variable', format: ['camelCase', 'UPPER_CASE', 'PascalCase'], leadingUnderscore: 'allow' },
        { selector: 'typeLike', format: ['PascalCase'] },
        { selector: 'enumMember', format: ['PascalCase', 'UPPER_CASE'] },
        { selector: 'import', format: ['camelCase', 'PascalCase'] },
      ],

      // Imports
      'import/order': ['error', {
        groups: ['builtin', 'external', 'internal', 'parent', 'sibling', 'index', 'object', 'type'],
        'newlines-between': 'always',
        alphabetize: { order: 'asc', caseInsensitive: true },
      }],
      'import/no-duplicates': 'error',
      'import/no-extraneous-dependencies': ['error', {
        devDependencies: [
          '**/*.{test,spec}.ts',
          '**/__tests__/**',
          '**/vitest.config.{ts,js,cjs,mjs}',
          '**/jest.config.{ts,js,cjs,mjs}',
          '**/scripts/**',
          '**/*.config.{ts,js,cjs,mjs}',
        ],
      }],
      'import/prefer-default-export': 'off',
      'import/no-default-export': 'error',
      'import/extensions': ['error', 'ignorePackages', {
        ts: 'never', tsx: 'never', js: 'never', mjs: 'never', cjs: 'never',
      }],

      // Unicorn
      'unicorn/prevent-abbreviations': ['error', {
        allowList: { args: true, env: true, params: true, props: true, Props: true, ref: true, Ref: true },
      }],
      'unicorn/filename-case': ['error', { cases: { kebabCase: true, pascalCase: true } }],
      'unicorn/no-null': 'off',
      'unicorn/prefer-module': 'off',
      'unicorn/prefer-top-level-await': 'off',

      // Security
      'security/detect-object-injection': 'off',
      'security/detect-non-literal-fs-filename': 'off',

      // Sonar
      'sonarjs/cognitive-complexity': ['error', 15],
      'sonarjs/no-duplicate-string': ['error', { threshold: 5 }],

      // Quality
      'no-console': ['warn', { allow: ['warn', 'error', 'info', 'debug'] }],
      'no-debugger': 'error',
      'no-alert': 'error',
      'no-var': 'error',
      'prefer-const': 'error',
      'prefer-arrow-callback': 'error',
      'prefer-template': 'error',
      'prefer-destructuring': ['error', { array: false, object: true }],
      'no-nested-ternary': 'error',
      'no-unneeded-ternary': 'error',
      'no-param-reassign': ['error', {
        props: true,
        ignorePropertyModificationsFor: ['acc', 'accumulator', 'draft', 'state'],
      }],
      eqeqeq: ['error', 'always'],
      curly: ['error', 'all'],

      // Complexity
      complexity: ['error', 10],
      'max-depth': ['error', 4],
      'max-lines': ['error', { max: 400, skipBlankLines: true, skipComments: true }],
      'max-lines-per-function': ['error', { max: 80, skipBlankLines: true, skipComments: true }],
      'max-params': ['error', 4],

      // Comments
      'spaced-comment': ['error', 'always', { markers: ['/'] }],
    },
  },
  // Vitest-specific override
  {
    files: ['**/*.{test,spec}.ts', '**/__tests__/**/*.ts'],
    languageOptions: {
      env: { 'vitest/globals': true },
    },
    plugins: { vitest },
    rules: {
      '@typescript-eslint/no-explicit-any': 'off',
      '@typescript-eslint/no-unsafe-assignment': 'off',
      '@typescript-eslint/no-unsafe-member-access': 'off',
      'sonarjs/no-duplicate-string': 'off',
      'max-lines-per-function': 'off',
    },
  },
  // Config files override
  {
    files: ['*.config.{ts,js,cjs,mjs}'],
    rules: { 'import/no-default-export': 'off' },
  },
];
