#!/usr/bin/env node
/* eslint-disable no-console */

import chalk from 'chalk';
import { program } from 'commander';

import { version } from './index.js';

// Set up the main program
program
  .name('envguard')
  .description('Local-first secret management for developers')
  .version(version)
  .option('-v, --verbose', 'Enable verbose logging')
  .hook('preAction', (thisCommand) => {
    const options = thisCommand.opts();
    if (options.verbose) {
      console.log(
        chalk.dim(
          `[EnvGuard v${version}] Running command: ${thisCommand.name()}`
        )
      );
    }
  });

// Placeholder commands that will be implemented
program
  .command('init')
  .description('Initialize EnvGuard in the current directory')
  .action(() => {
    console.log(chalk.blue('ğŸ”’ Initializing EnvGuard...'));
    console.log(chalk.yellow('âš ï¸  This command is not yet implemented'));
    console.log(
      chalk.dim('See the implementation guide for development progress')
    );
  });

program
  .command('set <key> <value>')
  .description('Store a secret in the OS keychain')
  .action((key: string, _value: string) => {
    console.log(chalk.blue(`ğŸ” Setting secret: ${key}`));
    console.log(chalk.yellow('âš ï¸  This command is not yet implemented'));
    console.log(
      chalk.dim('Secret storage will use OS keychain when implemented')
    );
  });

program
  .command('get <key>')
  .description('Retrieve a secret from the OS keychain')
  .option('-j, --json', 'Output as JSON')
  .action((key: string, options: { json?: boolean }) => {
    console.log(chalk.blue(`ğŸ” Getting secret: ${key}`));
    if (options.json) {
      console.log(chalk.dim('Will output as JSON when implemented'));
    }
    console.log(chalk.yellow('âš ï¸  This command is not yet implemented'));
  });

program
  .command('list')
  .description('List all stored secrets (keys only)')
  .action(() => {
    console.log(chalk.blue('ğŸ“‹ Listing stored secrets...'));
    console.log(chalk.yellow('âš ï¸  This command is not yet implemented'));
  });

program
  .command('status')
  .description('Show current EnvGuard status and configuration')
  .action(() => {
    console.log(chalk.blue('ğŸ“Š EnvGuard Status'));
    console.log(chalk.green(`âœ… Version: ${version}`));
    console.log(chalk.green(`âœ… Node.js: ${process.version}`));
    console.log(
      chalk.green(`âœ… Platform: ${process.platform} ${process.arch}`)
    );
    console.log(chalk.yellow('âš ï¸  Full functionality coming soon'));

    // Show development status
    console.log(chalk.dim('\nğŸ“‹ Implementation Progress:'));
    console.log(chalk.dim('  ğŸš§ Keychain integration (in progress)'));
    console.log(chalk.dim('  â³ CLI commands'));
    console.log(chalk.dim('  â³ Config validation'));
    console.log(chalk.dim('  â³ Runtime runners'));
  });

// Add help examples
program.addHelpText(
  'after',
  `
Examples:
  $ envguard status              Show current status
  $ envguard init                Initialize in current directory
  $ envguard set API_KEY abc123  Store a secret (when implemented)
  $ envguard get API_KEY         Retrieve a secret (when implemented)
  $ envguard list                List all secrets (when implemented)

Development:
  This is a development build. See .plan/implementation-guidebook.md
  for the full implementation roadmap and current progress.
`
);

// Parse CLI arguments
program.parse(process.argv);

// Show help if no arguments provided
if (process.argv.slice(2).length === 0) {
  program.outputHelp();
}
